package controlstatements;

public class Foorloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=5;i<=10;i++) {
			System.out.println(i);
		}
		System.out.println("*************************");
		
		for(int i=100;i>=1;i--) {
			System.out.println(i);
		}
	}

}